<template>  <div style="width: 600px">    <h1 class="text-center mb-5">{{ selectedCity.nome }} districts list, total {{ districts.length }}</h1>    <table class="table table-hover border mb-0 hover">      <thead>      <tr>        <th scope="col">NÂº</th>        <th class="text-start" scope="col">District</th>      </tr>      </thead>      <tbody>      <tr          class="text-align-left table-row"          v-for="district in districts"      >        <td>{{ district.id }}</td>        <td class="text-start">{{ district.nome }}</td>      </tr>      </tbody>    </table>    <nav v-if="finalIndex > 10" aria-label="Page navigation example">      <ul class="pagination pagination-color-black d-flex">        <li @click="methodPreviousPage" class="page-item"><a class="page-link" href="#">Previous</a></li>        <li v-for="(_, i) in Math.ceil(finalIndex / 10)" class="page-item" @click="methodHandleChangePage(i)">          <a class="page-link" href="#">{{ i + 1 }}</a>        </li>        <li @click="methodNextPage" class="page-item"><a class="page-link" href="#">Next</a></li>      </ul>    </nav>  </div></template><script lang="ts">export default {  name: "Table",  props: {    districts: Array,    finalIndex: Number,    selectedCity: Object,    methodNextPage: Function,    methodPreviousPage: Function,    methodHandleChangePage: Function,  },}</script><style scoped>li > a {  color: black;}.table-row {  cursor: pointer;}</style>